import os


def main(e):
	newfile = e[:-4]+"_adj.txt"
	os.system("touch "+newfile)
	fio = open(e,"r")
	fio1 = open(newfile,"w")
	lines = fio.readlines()
	temp = []
	for line in lines:
		list_line = line.split(' ')
		for word in list_line:
			if word in list_adj:
				if word in temp:
					pass
				else:
					temp.append(word)
			elif word in otheradj_list:
				if word in temp:
					pass
				else:
					temp.append(word)
			else:
				pass
	for i in temp:
		fio1.write(i+"\n")
	fio.close()
	fio1.close()


